mapper.select:
SELECT f_id, u_id
FROM follow
WHERE u_id = #{u_id}
AND f_id = #{f_id}

mapper.update:
DELETE
FROM follow
WHERE u_id = #{u_id}
AND f_id = #{f_id}

mapper.select:
SELECT uf_id, u_id
FROM un_follow
WHERE u_id = #{u_id}
AND uf_id = #{uf_id}

mapper.update:
DELETE
FROM un_follow
WHERE u_id = #{u_id}
AND uf_id = #{uf_id}
