mapper.select:
SELECT u_id, u_email, u_pw, u_phone_number, u_nickname, u_expire_member, u_is_admin, u_profile_pic, u_is_blocked
FROM member
WHERE u_email = #{u_email}

SELECT u_id, u_email, u_pw, u_phone_number, u_nickname, u_expire_member, u_is_admin, u_profile_pic, u_is_blocked
FROM member
WHERE u_id = #{u_id}

select u_email
from member
where u_phone_number = #{u_phone_number}

select u_email
from member
where u_email = #{u_email}

mapper.update:
update member
set u_pw = #{u_pw}
where u_email = #{u_email}

mapper.select:
select u_email
from member
where u_email = #{u_email}

select u_nickname
from member
where u_nickname = #{u_nickname}
